import React, { Component } from 'react';

class Input extends Component {
  render() {
    return (
      <div>
        <input type="text" value={this.props.value} placeholder="Enter value here" className="form-control"  onChange={this.props.updateval}/>
      </div>

    );
  }
}

export default Input;
